/**
 * HTML nodeType values that represent the type of the node
 */

export const ELEMENT_NODE = 1; // 表示元素，body、a、div
export const TEXT_NODE = 3; // 文字内容 <div>描述是这里面的内容</div>
export const COMMENT_NODE = 8; // 注释
export const DOCUMENT_NODE = 9; // document 节点
export const DOCUMENT_FRAGMENT_NODE = 11; // framenet 节点
